import React, { useState } from "react";
import styled from "styled-components";
import FundingDetailPresenter from "./FundingDetailPresenter";
import { cav } from "Caver/caver";
// import Caver from "caver-js";

const Container = styled.div``;

const FundingDetailContainer = () => {
    const abi = [
        {
            constant: false,
            inputs: [],
            name: "deposit",
            outputs: [],
            payable: true,
            stateMutability: "payable",
            type: "function",
        },
        {
            constant: false,
            inputs: [
                {
                    name: "_value",
                    type: "uint256",
                },
            ],
            name: "getRefund",
            outputs: [
                {
                    name: "",
                    type: "bool",
                },
            ],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: true,
            inputs: [
                {
                    name: "",
                    type: "address",
                },
            ],
            name: "contributions",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "count",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "currentBalance",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "getBalance",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
    ];

    //컨트랙트 주소.
    const contractAddress = "0x10e5ffaf9575c37e909ddf405a96c9ef1ecb3527";
    const [visible, setVisible] = useState(false);
    const [privkey, setPrivkey] = useState("");

    const handleKey = (e) => {
        setPrivkey(e.target.value);
    };

    const handleOkandCancel = async (e) => {
        e.preventDefault();
        setVisible(false);

        const deployer = cav.wallet.keyring.createFromPrivateKey(privkey);
        cav.wallet.add(deployer);

        const contractInstance = new cav.contract(abi, contractAddress);

        console.log(contractInstance);

        try {
            const value = await contractInstance.methods.deposit().send({
                from: deployer.address,
                gas: "200000",
                value: cav.utils.toPeb(1, "KLAY"),
            });
            alert("펀딩 성공!");
            console.log(value);
        } catch (e) {
            alert("펀딩 실패ㅜㅜ");
            console.log(e);
        }
    };

    const handleFunding = (e) => {
        setVisible(true);
    };

    const handleCancel = (e) => {};

    return (
        <Container>
            <FundingDetailPresenter
                visible={visible}
                privkey={privkey}
                handleFunding={handleFunding}
                handleCancel={handleCancel}
                handleOkandCancel={handleOkandCancel}
                handleKey={handleKey}
            ></FundingDetailPresenter>
        </Container>
    );
};

export default FundingDetailContainer;
