import React, { useState } from "react";
import styled from "styled-components";
import FundingDetailPresenter from "./FundingDetailPresenter";
import { cav } from "Caver/caver";

// import Caver from "caver-js";

const Container = styled.div``;

const FundingDetailContainer = () => {
    const abi = [
        {
            constant: true,
            inputs: [],
            name: "owner",
            outputs: [{ name: "", type: "address" }],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            inputs: [],
            payable: false,
            stateMutability: "nonpayable",
            type: "constructor",
        },
        {
            anonymous: false,
            inputs: [
                { indexed: true, name: "from", type: "address" },
                { indexed: true, name: "to", type: "address" },
                { indexed: false, name: "value", type: "uint256" },
            ],
            name: "Transfer",
            type: "event",
        },
        {
            constant: false,
            inputs: [
                { name: "restaurant", type: "address" },
                { name: "foodname", type: "string" },
                { name: "endTime", type: "uint256" },
                { name: "totalAmount", type: "uint256" },
                { name: "price", type: "uint256" },
            ],
            name: "createFunding",
            outputs: [],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: false,
            inputs: [
                { name: "a", type: "address" },
                { name: "f", type: "string" },
                { name: "_value", type: "uint256" },
            ],
            name: "findTarget",
            outputs: [{ name: "", type: "bool" }],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: false,
            inputs: [
                { name: "idx", type: "uint256" },
                { name: "_value", type: "uint256" },
            ],
            name: "UpdateValue",
            outputs: [{ name: "", type: "bool" }],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: false,
            inputs: [],
            name: "deposit",
            outputs: [],
            payable: true,
            stateMutability: "payable",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "getBalance",
            outputs: [{ name: "", type: "uint256" }],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "getBalanceUser",
            outputs: [{ name: "", type: "uint256" }],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: false,
            inputs: [{ name: "_value", type: "uint256" }],
            name: "transfer",
            outputs: [{ name: "", type: "bool" }],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
    ];

    //컨트랙트 주소.
    const contractAddress = "0x9b7489b6b928dc8b08a386f2dcd3d7615da1b241";
    const contractInstance = new cav.contract(abi, contractAddress);
    const [fundingvisible, setVisible] = useState(false);
    const [cancelVisible, setCancel] = useState(false);
    const [privkey, setPrivkey] = useState("");

    const [FundingDone, setFunding] = useState(false);

    const handleKey = (e) => {
        setPrivkey(e.target.value);
    };

    // 모달 창에서 취소버튼 눌렀을때

    const Cancel = (e) => {
        e.preventDefault();
        setVisible(false);
    };

    const amount = 0.1;

    // 펀딩하기 버튼에서 확인 버튼 눌렀을 때
    const handleOk = async (e) => {
        e.preventDefault();

        if (privkey === "") {
            alert("키를 입력해주세요!");
            return;
        }

        setVisible(false);

        const deployer = cav.wallet.keyring.createFromPrivateKey(privkey);
        cav.wallet.add(deployer);

        console.log(contractInstance);

        try {
            const value = await contractInstance.methods.deposit().send({
                from: deployer.address,
                gas: "250000",
                value: cav.utils.convertToPeb(String(amount), "KLAY"),
            });
            alert("펀딩 성공!");
            console.log(value);
        } catch (e) {
            alert("펀딩 실패ㅜㅜ");
            console.log(e);
        }
        setPrivkey("");
        // 펀딩 완료시.
    };

    const handleFunding = (e) => {
        setVisible(true);
    };

    const handleCancel = async (e) => {
        e.preventDefault();
        const deployer = cav.wallet.keyring.createFromPrivateKey(privkey);
        cav.wallet.add(deployer);
        const v = cav.utils.convertToPeb("0.1", "KLAY");

        try {
            const reci = await contractInstance.methods.transfer(v).send({
                from: deployer.address,
                gas: "250000",
            });
            alert("취소 완료!");
        } catch (e) {
            console.log(e);
        }
    };

    return (
        <Container>
            <FundingDetailPresenter
                fundingvisible={fundingvisible}
                privkey={privkey}
                handleFunding={handleFunding}
                handleCancel={handleCancel}
                handleOk={handleOk}
                handleKey={handleKey}
                Cancel={Cancel}
                cancelVisible={cancelVisible}
            ></FundingDetailPresenter>
        </Container>
    );
};

export default FundingDetailContainer;
