import React from "react";
import styled from "styled-components";
import BackGround from "assets/background.png";
import m1 from "assets/1.jpg";
import m2 from "assets/2.jpg";
import m3 from "assets/3.jpg";
import m4 from "assets/4.jpg";
import m5 from "assets/5.jpg";
import m6 from "assets/6.jpg";

const Container = styled.div`
    position: relative;
    background-image: url(${BackGround});
    height: 100vh;
    width: 100%;
    background-size: cover;
    background-repeat: no-repeat;
`;

const Title = styled.div`
    position: absolute;
    width: 100%;
    text-align: center;
    font-size: 70px;
    z-index: 0;
    top: 20px;
    padding: 20px 0;
`;

const Box = styled.div`
    position: absolute;
    bottom: 0;
    background-color: rgba(226, 226, 226, 0.5);
    height: 60%;
    width: 100%;
`;
const ImgBox = styled.div`
    width: 100%;
    height: 80%;
    display: flex;
    flex-wrap: nowrap;
    overflow-x: auto;

    position: absolute;
    bottom: 0;
    &::-webkit-scrollbar {
        display: none;
    }
`;
const Img = styled.img`
    flex: 0 0 auto;
    height: 100%;
    width: 100%;
    margin: 0 40px;
    border-radius: 10px;
    background-image: url(${(props) => props.img});
    background-repeat: no-repeat;
    background-size: cover;
`;
const HomePresenter = () => {
    return (
        <Container>
            <Box>
                <Title>할인 펀딩 목록</Title>
                <ImgBox>
                    <Img img={m1}></Img>
                    <Img img={m2}></Img>
                    <Img img={m6}></Img>
                    <Img img={m4}></Img>
                    <Img img={m1}></Img>
                    <Img img={m2}></Img>
                    <Img img={m6}></Img>
                    <Img img={m4}></Img>
                    <Img img={m3}></Img>
                    <Img img={m1}></Img>
                    <Img img={m2}></Img>
                    <Img img={m3}></Img>
                    <Img img={m1}></Img>
                    <Img img={m2}></Img>
                    <Img img={m3}></Img>
                    <Img img={m1}></Img>
                    <Img img={m2}></Img>
                    <Img img={m3}></Img>
                    <Img img={m1}></Img>
                    <Img img={m2}></Img>
                </ImgBox>
            </Box>
        </Container>
    );
};
export default HomePresenter;
