import React, { useState } from "react";
import styled from "styled-components";
import FundingDetailPresenter from "./FundingDetailPresenter";
import { cav } from "Caver/caver";
// import Caver from "caver-js";

const Container = styled.div``;

const FundingDetailContainer = () => {
    const abi = [
        {
            constant: false,
            inputs: [
                {
                    name: "restaurant",
                    type: "address",
                },
                {
                    name: "foodname",
                    type: "string",
                },
                {
                    name: "endTime",
                    type: "uint256",
                },
                {
                    name: "totalAmount",
                    type: "uint256",
                },
                {
                    name: "price",
                    type: "uint256",
                },
            ],
            name: "createFunding",
            outputs: [],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: false,
            inputs: [],
            name: "deposit",
            outputs: [],
            payable: true,
            stateMutability: "payable",
            type: "function",
        },
        {
            constant: false,
            inputs: [
                {
                    name: "a",
                    type: "address",
                },
                {
                    name: "f",
                    type: "string",
                },
                {
                    name: "_value",
                    type: "uint256",
                },
            ],
            name: "findTarget",
            outputs: [
                {
                    name: "",
                    type: "bool",
                },
            ],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: false,
            inputs: [
                {
                    name: "_value",
                    type: "uint256",
                },
            ],
            name: "transfer",
            outputs: [
                {
                    name: "",
                    type: "bool",
                },
            ],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: false,
            inputs: [
                {
                    name: "idx",
                    type: "uint256",
                },
                {
                    name: "_value",
                    type: "uint256",
                },
            ],
            name: "UpdateValue",
            outputs: [
                {
                    name: "",
                    type: "bool",
                },
            ],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            inputs: [],
            payable: false,
            stateMutability: "nonpayable",
            type: "constructor",
        },
        {
            anonymous: false,
            inputs: [
                {
                    indexed: true,
                    name: "from",
                    type: "address",
                },
                {
                    indexed: true,
                    name: "to",
                    type: "address",
                },
                {
                    indexed: false,
                    name: "value",
                    type: "uint256",
                },
            ],
            name: "Transfer",
            type: "event",
        },
        {
            constant: true,
            inputs: [],
            name: "getBalance",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "getBalanceUser",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "owner",
            outputs: [
                {
                    name: "",
                    type: "address",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
    ];

    //컨트랙트 주소.
    const contractAddress = "0x282d1e445ca46c72493024c7f50f7434e64c90dd";

    const [visible, setVisible] = useState(false);
    const [privkey, setPrivkey] = useState("");

    const [FundingDone, setFunding] = useState(false);

    const handleKey = (e) => {
        setPrivkey(e.target.value);
    };

    const handleOkandCancel = async (e) => {
        e.preventDefault();
        setVisible(false);

        const deployer = cav.wallet.keyring.createFromPrivateKey(privkey);
        cav.wallet.add(deployer);

        const contractInstance = new cav.contract(abi, contractAddress);

        console.log(contractInstance);

        try {
            const value = await contractInstance.methods.deposit().send({
                from: deployer.address,
                gas: "250000",
                value: cav.utils.toPeb(0.1, "KLAY"),
            });
            alert("펀딩 성공!");
            console.log(value);
        } catch (e) {
            alert("펀딩 실패ㅜㅜ");
            console.log(e);
        }
    };

    const handleFunding = (e) => {
        setVisible(true);
    };

    const handleCancel = (e) => {};

    return (
        <Container>
            <FundingDetailPresenter
                visible={visible}
                privkey={privkey}
                handleFunding={handleFunding}
                handleCancel={handleCancel}
                handleOkandCancel={handleOkandCancel}
                handleKey={handleKey}
            ></FundingDetailPresenter>
        </Container>
    );
};

export default FundingDetailContainer;
