import React, { useState } from "react";
import styled from "styled-components";
import FundingDetailPresenter from "./FundingDetailPresenter";
import { cav } from "Caver/caver";
import Caver from "caver-js";

const Container = styled.div``;

const getWallet = () => {
    if (cav.klay.accounts.wallet.length > 0) {
        return cav.klay.accounts.wallet[0];
    }
};

const FundingDetailContainer = () => {
    const abi = [
        {
            constant: false,
            inputs: [
                {
                    name: "_value",
                    type: "uint256",
                },
            ],
            name: "deposit",
            outputs: [],
            payable: true,
            stateMutability: "payable",
            type: "function",
        },
        {
            constant: false,
            inputs: [],
            name: "getRefund",
            outputs: [
                {
                    name: "",
                    type: "bool",
                },
            ],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: true,
            inputs: [
                {
                    name: "",
                    type: "address",
                },
            ],
            name: "contributions",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "creator",
            outputs: [
                {
                    name: "",
                    type: "address",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "currentBalance",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "state",
            outputs: [
                {
                    name: "",
                    type: "uint8",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
    ];

    //컨트랙트 주소.
    const Address = "0x12f3F9263f8Da2a7ac078C67a823Be9f4e2FCE44";
    const [visible, setVisible] = useState(false);
    const [privkey, setPrivkey] = useState("");

    const handleKey = (e) => {
        setPrivkey(e.target.value);
    };

    //0x8d7b30525c2c85ec1c78850d0fc96a524aaaa2a0e183f2f14c938b626e9f2cfc
    //0xd537ff4b3d5f6e79daa9312cc550c3b0c9e55c5de38188a368d4c947a56f5370
    const handleOkandCancel = async (e) => {
        e.preventDefault();
        setVisible(false);

        const walletInstance = cav.klay.accounts.privateKeyToAccount(privkey);
        cav.klay.accounts.wallet.add(walletInstance);

        const contractInstance = new cav.contract(abi, Address);

        console.log(walletInstance, contractInstance);
        const amount = 2;
        if (walletInstance) {
            try {
                contractInstance.methods
                    .deposit(1)
                    .send({
                        from: walletInstance.address,
                        gas: "250000",
                        value: cav.utils.toPeb(2, "KLAY"),
                    })
                    .once("transactionHash", (txHash) => {
                        console.log(`
                        Sending a transaction... (Call contract's function 'minus')
                        txHash: ${txHash}
                      `);
                    })
                    .once("receipt", (receipt) => {
                        console.log(
                            `
                      Received receipt which means your transaction(calling minus function)
                      is in klaytn block(#${receipt.blockNumber})
                    `,
                            receipt
                        );
                    })
                    .once("error", (error) => {
                        alert(error.message);
                    });
            } catch (e) {
                console.log(e);
            }
        }
    };

    const handleFunding = (e) => {
        setVisible(true);
    };

    const handleCancel = (e) => {};

    return (
        <Container>
            <FundingDetailPresenter
                visible={visible}
                privkey={privkey}
                handleFunding={handleFunding}
                handleCancel={handleCancel}
                handleOkandCancel={handleOkandCancel}
                handleKey={handleKey}
            ></FundingDetailPresenter>
        </Container>
    );
};

export default FundingDetailContainer;
