import React, { useState } from "react";
import styled from "styled-components";
import FundingDetailPresenter from "./FundingDetailPresenter";
import { cav } from "Caver/caver";

const Container = styled.div``;

const FundingDetailContainer = () => {
    const abi = [
        {
            constant: false,
            inputs: [],
            name: "deposit",
            outputs: [],
            payable: true,
            stateMutability: "payable",
            type: "function",
        },
        {
            constant: false,
            inputs: [],
            name: "getRefund",
            outputs: [
                {
                    name: "",
                    type: "bool",
                },
            ],
            payable: false,
            stateMutability: "nonpayable",
            type: "function",
        },
        {
            constant: true,
            inputs: [
                {
                    name: "",
                    type: "address",
                },
            ],
            name: "contributions",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "creator",
            outputs: [
                {
                    name: "",
                    type: "address",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "currentBalance",
            outputs: [
                {
                    name: "",
                    type: "uint256",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
        {
            constant: true,
            inputs: [],
            name: "state",
            outputs: [
                {
                    name: "",
                    type: "uint8",
                },
            ],
            payable: false,
            stateMutability: "view",
            type: "function",
        },
    ];

    //컨트랙트 주소.
    const address = "0xe319b2442e0c05f543c4d15528e26a7fd9894ac3";
    const [visible, setVisible] = useState(false);
    const [privkey, setPrivkey] = useState("");

    const handleKey = (e) => {
        setPrivkey(e.target.value);
    };

    //0x8d7b30525c2c85ec1c78850d0fc96a524aaaa2a0e183f2f14c938b626e9f2cfc

    const handleOkandCancel = async () => {
        setVisible(false);

        const walletInstance = cav.klay.accounts.privateKeyToAccount(privkey);
        cav.klay.accounts.wallet.add(walletInstance);
        console.log(walletInstance);
        sessionStorage.setItem(
            "walletInstance",
            JSON.stringify(walletInstance)
        );
        const contractInstance = new cav.contract(abi, address);
        console.log(contractInstance);

        await contractInstance.methods
            .deposit()
            .send({
                from: walletInstance.address,
                gas: "200000",
                value: cav.utils.toPeb(1, "KLAY"),
            })
            .once("transactionHash", (txHash) => {
                console.log(`
                Sending a transaction... (Call contract's function 'minus')
                txHash: ${txHash}
                `);
            })
            .once("receipt", (receipt) => {
                console.log(
                    `
                Received receipt which means your transaction(calling minus function)
                is in klaytn block(#${receipt.blockNumber})
            `,
                    receipt
                );
            })
            .once("error", (error) => {
                alert(error.message);
            });
    };

    const handleFunding = (e) => {
        setVisible(true);
    };

    const handleCancel = (e) => {};

    return (
        <Container>
            <FundingDetailPresenter
                visible={visible}
                privkey={privkey}
                handleFunding={handleFunding}
                handleCancel={handleCancel}
                handleOkandCancel={handleOkandCancel}
                handleKey={handleKey}
            ></FundingDetailPresenter>
        </Container>
    );
};

export default FundingDetailContainer;
