import React from "react";
import styled from "styled-components";
import m1 from "assets/6.jpg";
import { Button } from "antd";

const Container = styled.div`
    width: 80%;
    height: 100vh;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    margin: 0 auto;
    justify-items: center;
    align-items: center;
`;

const Button1 = styled.button`
    font-size: 40px;
    height: 70px;
    width: 300px;
    position: absolute;
    left: 0;
    top: 200px;
    background-color: green;
    cursor: pointer;
    border-radius: 10px;
    &:hover {
        transform: scale(0.95);
    }
`;

const Button2 = styled.button`
    font-size: 40px;
    height: 70px;
    width: 300px;
    position: absolute;
    left: 350px;
    top: 200px;
    background-color: green;
    cursor: pointer;
    border-radius: 10px;
    &:hover {
        transform: scale(0.95);
    }
`;

const Img = styled.div`
    height: 80%;
    width: 80%;
    margin-right: 0px;
    background-image: url(${m1});
    background-repeat: no-repeat;
    background-size: cover;
`;

const Title = styled.div`
    position: absolute;
    top: 70px;
    font-size: 50px;
`;

const Comment = styled.div`
    width: 100%;
    height: 100%;
    position: relative;
`;

const Info = styled.div``;

const Owner = styled.div`
    position: absolute;
    font-size: 48px;
    color: #047337;
    top: 20px;
    left: 100px;
`;
const Detail = styled.div`
    font-size: 24px;
    position: absolute;
    top: 100px;
    left: 100px;
`;

const Corona = styled.div`
    position: absolute;
    bottom: 0;
    left: 100px;
    color: #000000;
    font-size: 14px;
`;

const Box1 = styled.div`
    width: 100%;
    height: 100%;
    position: relative;
`;
const FundingDetailPresenter = () => {
    return (
        <Container>
            <Img></Img>
            <Box1>
                <Title>쇼타 돈부리_명륜캠</Title>
                <Button1>펀딩하기</Button1>
                <Button2>환불받기</Button2>
            </Box1>
            <Comment>
                <Owner>주인장의 말</Owner>
                <Detail>
                    안녕하세요. 성균관대학교 학생 여러분! 쇼타돈부리 사장
                    000입니다.
                    <br></br>
                    ‘성대한 할인’을 통해 12월 5일 ~ 12월 9일 동안 할인된 가격에
                    음식을 판매합니다. <br></br>
                    <br></br>
                    가츠동 6500 -> 한정판 20개<br></br>
                    4000원 치즈 가츠동 7000 -> 한정판 20개 5500원 <br></br>
                    <br></br>
                    많은 관심 부탁드립니다!!
                </Detail>
                <Corona>
                    성대한 할인에 참여하는 모든 음식점들은 코로나19 방역 수칙을
                    철저하게 준수하고 주기적으로 소독을 실시하며 위생을 우선으로
                    할 것을 약속합니다.{" "}
                </Corona>
            </Comment>
            <Info>hihi</Info>
        </Container>
    );
};

export default FundingDetailPresenter;
